graphql.query:
mutation createAccessGroup ($after: String, $filter: PodFilter, $first: Int, $order: PodOrder, $after1: String, $filter1: RoleFilter, $first1: Int, $order1: RoleOrder, $after2: String, $filter2: UserFilter, $first2: Int, $order2: UserOrder, $input: CreateAccessGroupInput!) {
    createAccessGroup (input: $input) {
        accessGroup {
            createdAt
            description
            id
            isDefault
            name
            pods (after: $after, filter: $filter, first: $first, order: $order) {
                totalCount
            }
            roles (after: $after1, filter: $filter1, first: $first1, order: $order1) {
                totalCount
            }
            ssoGroups
            updatedAt
            users (after: $after2, filter: $filter2, first: $first2, order: $order2) {
                totalCount
            }
        }
        success
    }
}

